function createRipple(t){const e=t.currentTarget,o=document.createElement("span"),n=Math.max(e.clientWidth,e.clientHeight),l=n/2;o.style.width=o.style.height=`${n}px`,o.style.left=`${t.offsetX-l}px`,o.style.top=`${t.offsetY-l}px`,o.classList.add("ripple");const c=e.querySelector(".ripple");c&&c.remove(),e.appendChild(o)}function closeModal(t){const e=document.getElementById(t);e.style.display="none"}function openModal(t){const e=document.getElementById(t);e.style.display="block"}const buttons=document.querySelectorAll("button[data-ripple]");for(const t of buttons)t.classList.contains("tooltip")||t.addEventListener("click",createRipple);const openModalButtons=document.querySelectorAll("button[data-modal]");for(const t of openModalButtons)t.addEventListener("click",()=>{const e=t.getAttribute("data-modal");openModal(e),document.addEventListener("keydown",t=>"Escape"===t.key?closeModal(e):null)});const closeButtons=document.querySelectorAll(".close-btn");for(const t of closeButtons)t.addEventListener("click",()=>{const e=t.getAttribute("data-modal");closeModal(e),document.addEventListener("keydown",t=>"Escape"===t.key?closeModal(e):null)});